    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}{{product.name}} - La Boutique Française{% endblock %}

{% block content %}


<div class=\"row\">
    
    

    <div class=\"col-md-5\">
        <img src=\"/uploads/{{ product.illustration }}\" alt=\"{{ product.name }}\" class=\"img-fluid\">
    </div>
    
   
    <div class=\"col-md-7 my-auto\">
        <h2>{{ product.name }}</h2>
        <p>{{ product.subtitle}}</p>
        <span class=\"product-page-price\">{{ (product.price / 100)|number_format(2, ',', '')|default('0.00') }}€</span>
        <hr>
        <p>{{ product.description }}</p>
        <a href=\"{{ path('add_to_cart', { 'id' : product.id}) }}\" class=\"btn btn-primary\">Ajouter au panier</a>
       
    </div>
</div>
<hr>
<h1>Nos meilleurs ventes</h1>
<p>Découvrez les articles les plus vendus.</p>
 <div class=\"row\">
      {% for product in products %}
        <div class=\"col-md-3\">
          {% include 'product/single_product.html.twig' %}
        </div>
      {% endfor %}
    </div>




{% endblock %}
", "product/show.html.twig", "/var/www/html/la-boutique/templates/product/show.html.twig");
